  font-size: 16px;
  line-height: 1.7em;
  margin: 12px 0;

  :deep(code) {
    color: #476582;
    padding: 4px 8px;
    margin: 0 4px;
    font-size: 0.85em;
    background-color: rgba(27, 31, 35, 0.05);
    border-radius: 3px;
  }

  a {
    text-decoration: none;
    color: #5e7ce0;
    cursor: pointer;
  }

  ul,
  ol {
    list-style: disc;
    list-style-position: inside;
  }
}

.theme-dark {
  .demo-desc {
    code {
      background: #333333;
    }
  }
}

.b-a-success {
  animation: border-trans 3s;
}

